import { Grid, Col, Row } from 'react-styled-flexboxgrid';
import styled, { createGlobalStyle } from 'styled-components';
import Head from 'next/head';

const GlobalStyle = createGlobalStyle`
  body {
    background-color: #f4f9ff;
    font-family: 'Open Sans', sans-serif;
  }
`;

const CircularImage = styled.img`
  width: 90px;
  height: auto;
  font-size: 1rem;
  border-radius: 500rem;
  display: inline-block;
  vertical-align: middle;
  margin-right: 25px;
`;

const TitleCol = styled(Col)`
  padding-top: 21px;
`;

const H1 = styled.span`
  margin: 0em;
  font-size: 1.7rem;
  font-weight: 700;
  letter-spacing: 2px;
}`;

const Button = styled.button`
  width: 100%;
  margin-bottom: 0.75em;
  margin-right: 0.25em;
  cursor: pointer;
  display: inline-block;
  min-height: 1em;
  outline: 0;
  border: none;
  vertical-align: baseline;
  padding: .78571429em 1.5em .78571429em;
  line-height: 1em;
  font-style: normal;
  text-align: center;
  text-decoration: none;
  -webkit-tap-highlight-color: transparent;
  box-shadow: 0 0 0 2px #2185d0 inset!important;
  color: #2185d0!important;
  background: transparent none!important;
  font-weight: bold;
`;

const Divider = styled.div`
  margin: 1rem 0;
  line-height: 1;
  height: 0;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: .05em;
  color: rgba(0,0,0,.85);
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  font-size: 1rem;
  border-top: 2px solid #cde1fa;
  border-bottom: 2px solid rgba(255,255,255,.1);
`;

const BackButton = styled.button`
  width: 100%;
  margin-bottom: 0.75em;
  margin-right: 0.25em;
  cursor: pointer;
  display: inline-block;
  min-height: 1em;
  outline: 0;
  border: none;
  vertical-align: baseline;
  padding: .78571429em 1.5em .78571429em;
  line-height: 1em;
  font-style: normal;
  text-align: center;
  text-decoration: none;
  -webkit-tap-highlight-color: transparent;
  box-shadow: 0 0 0 2px #2185d0 inset!important;
  color: #2185d0!important;
  background: transparent none!important;
  font-weight: bold;
`;

const Index = () => (
  <Grid>
    <GlobalStyle />
    <Head>
      <link href='https://fonts.googleapis.com/css?family=Open+Sans' rel='stylesheet' type='text/css' />
    </Head>
    <Row>
      <TitleCol xs={12} sm={3} md={2} lg={10}>
        <CircularImage src="https://semantic-ui.com/images/wireframe/square-image.png" />
        <H1>Alita: Battle Angel</H1>
      </TitleCol>
      <Col xs={6} sm={3} md={2} lg={2}>
        <Button>Play Video</Button>
        <Button>Watch Later</Button>
        <Button>Share</Button>
      </Col>
    </Row>
    <Divider />
  </Grid>
);

export default Index;
